declare module 'react-fit' {
	import 'react-fit';

	interface Spacing {
		bottom: number
		left: number
		right: number
		top: number
	}

	interface FitProps {
		children: JSX.Element
		testID?: string,
		invertAxis?: boolean,
		invertSecondaryAxis?: boolean,
		mainAxis?: 'x' | 'y',
		spacing?: number | Spacing
	}

	export default function Fit( props: FitProps ): JSX.Element;
}
